var get_all_folder_names = function(dir, filelist) {
  var fs = fs || require('fs'),
      files = fs.readdirSync(dir);
  filelist = filelist || [];
  files.forEach(function(file) {
    if (fs.statSync(dir + file).isDirectory()) {
      filelist = get_all_folder_names(dir + file + '/', filelist);
    }
    else {
      filelist.push(dir);
    }
  });
  return filelist;
};



module.exports = get_all_folder_names
